sysauto_script.script:
var source = new GlideRecord('x_58872_needit_needit');
source.addEncodedQuery('u_requested_for_emailISEMPTY');
source.query();
while(source.next())
	{
		//var name = source.user_name;
		var contact = new GlideRecord('sys_user');
		//contact.addQuery('first_name', name[0]);
		//contact.addQuery('last_name', name[1]);
		gs.info('About to search for ->'+source.u_requested_for);
		contact.addQuery('sys_id', source.u_requested_for);
		contact.query();
		if (contact.next())
			{
				gs.info('The email is->'+contact.email);
				source.u_requested_for_email = contact.email;
				source.update();
			}

	}
